                    <input onChange ={event=> setThePassword(event.target.value)} placeholder='Enter Username' type="password" name="" required="" />
                    {error&&thePassword.length<=0?
                    <label>Password</label>: ""}
                </div>
                <a href="">
                <span></span>
                <span></span>
                <span></span>
                <span></span>
                    Sign In
                </a>
            </form> 
        </div>
    )
}

export default Signin;
